import styled from 'styled-components';

export const FiltersWrapper = styled.section`
  width: 17%;
  height: 100%;
  padding: 10pxpx;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #A65FD4;
`

export const FiltersContainer = styled.div`
position: relative;
`

export const InputFilter = styled.select`
  width: 100%;
  height: 100%;
  border: none;
  background-color: transparent;
  border-radius: 4px;
  font-size: 18px;
  font-weight: 500;
  min-width: 200px;
  padding: 10px 5px;
  box-shadow: 0 0 0 1px rgba(113 119 144, 0.25);
  color: #f9fafb;
  transition: all 0.3s ease-in-out;
  &:focus{
    outline: none;
    box-shadow: 0 0 0 1px rgba(113 119 144, 0.5);
`

export const ResetBtn = styled.button`
  border: none;
  background-color: #663C80;
  font-size: 16px;
  border-radius: 4px;
  padding: 10px 15px;
  margin-top: 30px;
  min-width: 200px;
  transition: transform 0.3s ease-in-out;
  cursor: pointer;
  &:hover{
   transform: scale(1.05);
  }
`
